import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";
import CardPersonalizado from "../CardPersonalizado/CardPersonalizado"; // Verifique o caminho se necessário

// Importe suas imagens aqui
import imagemCard1 from "../../assets/image/image1.jpg";
import imagemCard2 from "../../assets/image/image2.jpg";
import imagemCard3 from "../../assets/image/image3.jpg";

const ProductCards = () => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // md = <960px

    const backgroundColorCardSection = "#ebd4d9";

    return (
        <Box
            sx={{
                width: "100vw",
                // minHeight: "100vh" aqui garante que a seção ocupe a altura total,
                // eliminando espaços entre componentes se o conteúdo for menor.
                minHeight: "100vh",
                display: "flex",
                flexDirection: "column",
                alignItems: "center", // Centraliza o conteúdo horizontalmente
                justifyContent: "center", // Centraliza o conteúdo verticalmente se houver espaço extra
                overflow: "hidden",
                backgroundColor: backgroundColorCardSection,
                // Removido padding aqui, pois o container interno vai controlar
                // O padding que você queria "entre as bordas" agora é gerenciado pelo `padding` do `innerContainer` abaixo
            }}
        >
            {/* Novo Container para o Conteúdo Centralizado (Título e Cards) */}
            <Box
                sx={{
                    width: "100%", // Ocupa a largura total do pai
                    maxWidth: "1200px", // Define a largura máxima para o conteúdo central
                    // Padding responsivo para garantir distância das bordas laterais da viewport
                    padding: { xs: "40px 20px", md: "60px 40px" },
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center", // Centraliza itens (título e container de cards)
                    flexGrow: 1, // Permite que esta box cresça e ocupe o espaço restante verticalmente
                    justifyContent: "center", // Centraliza o conteúdo (título + cards) verticalmente dentro desta box
                }}
            >
                <Typography
                    variant="h2"
                    component="h2"
                    gutterBottom
                    sx={{
                        fontFamily: "tan-mon-cheri, sans-serif",
                        color: "#402620",
                        fontSize: isMobile ? "3rem" : "5rem",
                        textAlign: "center",
                        marginBottom: { xs: "30px", md: "40px" },
                        width: "100%", // Garante que o título ocupe a largura total do seu pai com padding
                    }}
                >
                    Nossos Diferenciais
                </Typography>

                <Box
                    sx={{
                        display: "flex",
                        flexDirection: isMobile ? "column" : "row",
                        justifyContent: "center", // Centraliza os cards horizontalmente
                        alignItems: "stretch", // Cards com a mesma altura
                        flexWrap: "wrap", // Permite que os cards quebrem a linha (essencial para mobile)
                        gap: { xs: "0", md: "30px" }, // **Importante:** Gap horizontal em desktop, 0 em mobile (usaremos marginBottom no card em mobile)
                        width: "100%", // Ocupa toda a largura do seu maxWidth pai
                        // Removido minHeight daqui, pois o flexGrow 1 do container pai e o minHeight do componente raíz já cuidam disso.
                    }}
                >
                    {/* Cards instanciados */}
                    <CardPersonalizado
                        imagem={imagemCard1}
                        titulo="Variedade Incrível"
                        descricao="Encontre todos os doces que você ama e descubra novos favoritos em nossa vasta seleção."
                        formatoImagem="quadrado"
                    />

                    <CardPersonalizado
                        imagem={imagemCard2}
                        titulo="Atendimento Personalizado"
                        descricao="Nossa equipe está pronta para te ajudar a encontrar o doce perfeito para qualquer ocasião."
                        formatoImagem="portal"
                    />

                    <CardPersonalizado
                        imagem={imagemCard3}
                        titulo="Qualidade e Frescor"
                        descricao="Garantimos doces sempre frescos e de alta qualidade para sua total satisfação."
                        formatoImagem="redondo"
                    />
                </Box>
            </Box>
        </Box>
    );
};

export default ProductCards;
