import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";

const CardPersonalizado = ({ imagem, titulo, descricao, formatoImagem }) => {
    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

    let borderRadiusStyle = {};

    switch (formatoImagem) {
        case "quadrado":
            borderRadiusStyle = { borderRadius: "0" };
            break;
        case "portal":
            borderRadiusStyle = {
                borderTopLeftRadius: "50% 20%",
                borderTopRightRadius: "50% 20%",
                borderBottomLeftRadius: "0",
                borderBottomRightRadius: "0",
            };
            break;
        case "redondo":
            borderRadiusStyle = { borderRadius: "50%" };
            break;
        default:
            borderRadiusStyle = { borderRadius: "0" };
    }

    return (
        <Box
            sx={{
                // Os cards devem dividir o espaço igualmente, sem margens ou gaps internos
                flex: 1, // Faz com que o card ocupe o espaço disponível
                minWidth: {
                    xs: "100%",
                    sm: "calc(50% - 0px)",
                    md: "calc(33.33% - 0px)",
                }, // Calcula a largura exata
                // Removido 'margin' e 'gap' aqui para que o container pai controle o espaçamento
                padding: "20px",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                backgroundColor: "#fff",
                // Remover borderRadius para o card em si se quiser que eles "grudem" visualmente
                // Se quiser um BOX individual, pode manter, mas para "grudar", é melhor que as bordas do box sejam 0
                borderRadius: "0", // Ou manter '8px' se o espaçamento de borda for desejado
                boxShadow: "none", // Remover sombra para "grudar" ou ajustar para ser mais sutil
                borderRight: {
                    xs: "none",
                    sm:
                        formatoImagem !== "redondo" || isSmallScreen
                            ? "1px solid rgba(0, 0, 0, 0.1)"
                            : "none",
                }, // Linha divisória
                borderBottom: "1px solid rgba(0, 0, 0, 0.1)", // Linha divisória na parte de baixo
            }}
        >
            <Box
                sx={{
                    width: isSmallScreen ? "120px" : "180px",
                    height: isSmallScreen ? "120px" : "180px",
                    overflow: "hidden",
                    marginBottom: "15px",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    ...borderRadiusStyle,
                }}
            >
                <img
                    src={imagem}
                    alt={titulo}
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                    }}
                />
            </Box>
            <Typography
                variant="h5"
                component="h3"
                gutterBottom
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#402620",
                    mt: 1,
                }}
            >
                {titulo}
            </Typography>
            <Typography
                variant="body1"
                sx={{ fontFamily: "Roboto, sans-serif", color: "#666" }}
            >
                {descricao}
            </Typography>
        </Box>
    );
};

export default CardPersonalizado;
