import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";
import CardPersonalizado from "../CardPersonalizado/CardPersonalizado"; // Verifique o caminho se necessário

// Importe suas imagens aqui (exemplo de nomes)
import imagemCard1 from "../../assets/image/image1.jpg"; // Substitua pelo caminho real das suas imagens
import imagemCard2 from "../../assets/image/image2.jpg";
import imagemCard3 from "../../assets/image/image3.jpg";

const ProductCards = () => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // md = <960px

    const backgroundColorCardSection = "#ebd4d9";

    return (
        <Box
            sx={{
                width: "100vw", // Mantém 100% da largura da viewport
                padding: { xs: "40px 20px", md: "60px 40px" }, // Padding responsivo (vertical e horizontal)
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                overflow: "hidden", // Garante que nada extravase
                backgroundColor: backgroundColorCardSection,
            }}
        >
            <Typography
                variant="h2"
                component="h2"
                gutterBottom
                sx={{
                    fontFamily: "tan-mon-cheri, sans-serif",
                    color: "#402620",
                    fontSize: isMobile ? "3rem" : "5rem",
                    textAlign: "center",
                    marginBottom: { xs: "30px", md: "40px" }, // Margem inferior responsiva
                    maxWidth: "800px", // Limita a largura do título para não ficar muito esticado
                }}
            >
                Nossos Diferenciais
            </Typography>

            <Box
                sx={{
                    display: "flex",
                    flexDirection: isMobile ? "column" : "row",
                    justifyContent: "center",
                    alignItems: "center",
                    flexWrap: "wrap", // Permite que os cards quebrem a linha
                    gap: { xs: "20px", md: "30px" }, // Espaçamento entre os cards responsivo
                    maxWidth: "1200px", // **Chave para o alinhamento!** Limita a largura do container dos cards
                    width: "100%", // Garante que o container ocupe toda a largura disponível até o maxWidth
                }}
            >
                {/* Cards instanciados */}
                <CardPersonalizado
                    imagem={imagemCard1}
                    titulo="Variedade Incrível"
                    descricao="Encontre todos os doces que você ama e descubra novos favoritos em nossa vasta seleção."
                    formatoImagem="quadrado"
                />

                <CardPersonalizado
                    imagem={imagemCard2}
                    titulo="Atendimento Personalizado"
                    descricao="Nossa equipe está pronta para te ajudar a encontrar o doce perfeito para qualquer ocasião."
                    formatoImagem="portal"
                />

                <CardPersonalizado
                    imagem={imagemCard3}
                    titulo="Qualidade e Frescor"
                    descricao="Garantimos doces sempre frescos e de alta qualidade para sua total satisfação."
                    formatoImagem="redondo"
                />
            </Box>
        </Box>
    );
};

export default ProductCards;
