import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";

const CardPersonalizado = ({ imagem, titulo, descricao, formatoImagem }) => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // md = <960px

    let borderRadiusStyle = {};
    // Aumenta o tamanho da imagem nos cards
    // Tamanho ligeiramente maior para desktop e um bom tamanho para mobile
    let imageContainerSize = isMobile ? "160px" : "230px"; // Ajustado o tamanho da imagem

    switch (formatoImagem) {
        case "quadrado":
            borderRadiusStyle = { borderRadius: "0" };
            break;
        case "portal":
            borderRadiusStyle = {
                borderTopLeftRadius: "50% 20%",
                borderTopRightRadius: "50% 20%",
                borderBottomLeftRadius: "0",
                borderBottomRightRadius: "0",
            };
            break;
        case "redondo":
            borderRadiusStyle = { borderRadius: "50%" };
            break;
        default:
            borderRadiusStyle = { borderRadius: "0" };
    }

    return (
        <Box
            sx={{
                // Largura dos cards:
                // Desktop (md e acima): Exatamente 1/3 da largura do container, subtraindo o gap.
                // Mobile (xs, sm): Ocupa 100% para empilhar.
                // A chave é que a soma das larguras + gaps precisa bater perfeitamente no container pai.
                width: isMobile ? "100%" : "calc(33.3333% - (30px * 2 / 3))", // 30px é o gap horizontal do pai
                // Para 3 cards em linha, 2 gaps, então cada card 'perde' (2/3) do gap
                // Ex: (30px * 2 / 3) = 20px. Então calc(33.3333% - 20px)
                maxWidth: "350px", // Limita a largura máxima do card individualmente em desktop
                minWidth: "280px", // Garante que o card não fique muito pequeno em larguras intermediárias
                // Removido padding para deixar o container pai controlar o espaçamento lateral total
                padding: "20px", // Padding interno do conteúdo do card
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                backgroundColor: isMobile ? "#fff" : "transparent", // Fundo branco no mobile para toque visual
                borderRadius: isMobile ? "8px" : "0", // Borda arredondada no mobile
                boxShadow: isMobile
                    ? "0px 4px 15px rgba(0, 0, 0, 0.1)"
                    : "none", // Sombra no mobile
                position: "relative",
                // Adicionando um pequeno espaçamento vertical entre os cards em mobile para evitar que fiquem colados
                marginBottom: isMobile ? "30px" : "0", // Gap vertical em mobile
                // Último card em mobile não tem margem inferior
                "&:last-of-type": {
                    marginBottom: isMobile ? "0" : "0",
                },
            }}
        >
            <Box
                sx={{
                    width: imageContainerSize,
                    height: imageContainerSize,
                    overflow: "hidden",
                    marginBottom: "15px",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    position: "relative",
                    ...borderRadiusStyle,
                }}
            >
                <img
                    src={imagem}
                    alt={titulo}
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
                {/* Outline / Borda Suspensa */}
                <Box
                    sx={{
                        position: "absolute",
                        top: "-5px",
                        left: "-5px",
                        right: "-5px",
                        bottom: "-5px",
                        border: "2px solid #402620",
                        pointerEvents: "none",
                        zIndex: 1,
                        ...borderRadiusStyle,
                    }}
                />
            </Box>
            <Typography
                variant="h5"
                component="h3"
                gutterBottom
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#402620",
                    mt: 1,
                }}
            >
                {titulo}
            </Typography>
            <Typography
                variant="body1"
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#666",
                    paddingX: "10px",
                }}
            >
                {" "}
                {/* Padding horizontal no texto */}
                {descricao}
            </Typography>
        </Box>
    );
};

export default CardPersonalizado;
