import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";
import CardPersonalizado from "./CardPersonalizado"; // Importe o componente do card

// Importe suas imagens aqui (exemplo de nomes)
import imagemCard1 from "../../assets/image/iamge1.jpg"; // Substitua pelo caminho real das suas imagens
import imagemCard2 from "../../assets/image/card2.jpg";
import imagemCard3 from "../../assets/image/card3.jpg";

const ProductCards = () => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md"));

    return (
        <Box
            sx={{
                width: "100vw",
                padding: "60px 20px",
                backgroundColor: "#f9f9f9", // Cor de fundo da seção
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                overflow: "hidden",
            }}
        >
            <Typography
                variant="h2"
                component="h2"
                gutterBottom
                sx={{
                    fontFamily: "tan-mon-cheri, sans-serif",
                    color: "#402620",
                    fontSize: isMobile ? "3rem" : "5rem",
                    textAlign: "center",
                    marginBottom: "40px",
                }}
            >
                Nossos Diferenciais
            </Typography>

            <Box
                sx={{
                    display: "flex",
                    flexDirection: isMobile ? "column" : "row",
                    justifyContent: "center",
                    alignItems: "center",
                    flexWrap: "wrap", // Permite que os cards quebrem a linha em telas menores
                    gap: "30px", // Espaçamento entre os cards
                }}
            >
                {/* Primeiro Card: Imagem Quadrada */}
                <CardPersonalizado
                    imagem={imagemCard1} // Substitua pelo seu caminho real
                    titulo="Variedade Incrível"
                    descricao="Encontre todos os doces que você ama e descubra novos favoritos em nossa vasta seleção."
                    formatoImagem="quadrado"
                />

                {/* Segundo Card: Imagem de Portal (quadrado com pontas superiores arqueadas) */}
                <CardPersonalizado
                    imagem={imagemCard2} // Substitua pelo seu caminho real
                    titulo="Atendimento Personalizado"
                    descricao="Nossa equipe está pronta para te ajudar a encontrar o doce perfeito para qualquer ocasião."
                    formatoImagem="portal"
                />

                {/* Terceiro Card: Imagem Redonda */}
                <CardPersonalizado
                    imagem={imagemCard3} // Substitua pelo seu caminho real
                    titulo="Qualidade e Frescor"
                    descricao="Garantimos doces sempre frescos e de alta qualidade para sua total satisfação."
                    formatoImagem="redondo"
                />
            </Box>
        </Box>
    );
};

export default ProductCards;
