import React from "react";
import fini from "../../assets/fini.jpg";
import {
    Box,
    Typography,
    Button,
    useTheme,
    useMediaQuery,
} from "@mui/material";

const HeroSection = () => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // md = <960px

    return (
        <Box
            sx={{
                display: "flex",
                flexDirection: isMobile ? "column" : "row", // ← muda layout
                width: "100vw",
                minHeight: "100vh",
                overflow: "hidden",
            }}
        >
            {/* Texto */}
            <Box
                sx={{
                    flex: 1,
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: isMobile ? "center" : "flex-start",
                    textAlign: isMobile ? "center" : "left",
                    padding: 4,
                    backgroundColor: "#c4b3dd",
                }}
            >
                <Typography variant="h3" gutterBottom>
                    Bem-vindo à Minha Landing Page
                </Typography>
                <Typography variant="h6" gutterBottom>
                    Aqui você encontra os melhores projetos e ideias criativas.
                </Typography>
                <Button variant="contained" color="primary">
                    Saiba Mais
                </Button>
            </Box>

            {/* Imagem */}
            <Box
                sx={{
                    flex: 1,
                    height: isMobile ? "300px" : "100vh", // altura menor no mobile
                    overflow: "hidden",
                }}
            >
                <img
                    src={fini}
                    alt="Fini"
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
            </Box>
        </Box>
    );
};

export default HeroSection;
