import React from "react";
import fini from "../../assets/fini.jpg";
import { Box, Grid, Typography, Button } from "@mui/material";

const HeroSection = () => {
    return (
        <Box sx={{ width: "100vw", minHeight: "100vh", overflow: "hidden" }}>
            <Grid
                container
                sx={{
                    minHeight: "100vh", // ← força altura mínima total
                }}
            >
                {/* Texto à esquerda */}
                <Grid
                    item
                    xs={12}
                    md={6}
                    sx={{
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "center",
                        alignItems: { xs: "center", md: "flex-start" },
                        textAlign: { xs: "center", md: "left" },
                        padding: 4,
                        backgroundColor: "#c4b3dd",
                        minHeight: "100vh", // ← força altura mesmo em telas grandes
                    }}
                >
                    <Typography variant="h3" gutterBottom>
                        Bem-vindo à Minha Landing Page
                    </Typography>
                    <Typography variant="h6" gutterBottom>
                        Aqui você encontra os melhores projetos e ideias
                        criativas.
                    </Typography>
                    <Button variant="contained" color="primary">
                        Saiba Mais
                    </Button>
                </Grid>

                {/* Imagem à direita */}
                <Grid item xs={12} md={6} sx={{ minHeight: "100vh" }}>
                    <Box
                        sx={{
                            height: "100%",
                            width: "100%",
                            overflow: "hidden",
                        }}
                    >
                        <img
                            src={fini}
                            alt="Fini"
                            style={{
                                width: "100%",
                                height: "100%",
                                objectFit: "cover",
                                display: "block", // ← evita bug de renderização invisível
                            }}
                        />
                    </Box>
                </Grid>
            </Grid>
        </Box>
    );
};

export default HeroSection;
