import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";

const CardPersonalizado = ({ imagem, titulo, descricao, formatoImagem }) => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // md = <960px

    let borderRadiusStyle = {};
    let imageContainerSize = isMobile ? "160px" : "230px"; // Tamanho da imagem

    switch (formatoImagem) {
        case "quadrado":
            borderRadiusStyle = { borderRadius: "0" };
            break;
        case "portal":
            borderRadiusStyle = {
                borderTopLeftRadius: "50% 20%",
                borderTopRightRadius: "50% 20%",
                borderBottomLeftRadius: "0",
                borderBottomRightRadius: "0",
            };
            break;
        case "redondo":
            borderRadiusStyle = { borderRadius: "50%" };
            break;
        default:
            borderRadiusStyle = { borderRadius: "0" };
    }

    return (
        <Box
            sx={{
                // Largura dos cards:
                // Desktop (md e acima): Exatamente 1/3 da largura do container, subtraindo o gap.
                // Mobile (xs, sm): Ocupa 100% menos o padding horizontal do container pai.
                width: isMobile ? "100%" : "calc(33.3333% - (30px * 2 / 3))",
                maxWidth: "350px", // Limita a largura máxima do card individualmente em desktop
                minWidth: "280px", // Garante que o card não fique muito pequeno em larguras intermediárias
                padding: "20px", // Padding interno do conteúdo do card
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                // REMOVIDO: backgroundColor: isMobile ? '#fff' : 'transparent', // Fundo branco no mobile para toque visual
                backgroundColor: "transparent", // AGORA SEMPRE TRANSPARENTE
                borderRadius: "0", // AGORA SEMPRE 0, não mais arredondado em mobile
                boxShadow: "none", // AGORA SEMPRE NONE, não mais sombra em mobile
                position: "relative",
                // Adicionando um espaçamento vertical consistente em mobile
                // O gap do container pai não será usado em mobile para vertical,
                // então o card precisa definir sua própria margem inferior.
                marginBottom: isMobile ? "30px" : "0", // Gap vertical em mobile
                // O último card em mobile não terá margem inferior
                "&:last-of-type": {
                    marginBottom: isMobile ? "0" : "0",
                },
            }}
        >
            <Box
                sx={{
                    width: imageContainerSize,
                    height: imageContainerSize,
                    overflow: "hidden",
                    marginBottom: "15px",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    position: "relative",
                    ...borderRadiusStyle,
                }}
            >
                <img
                    src={imagem}
                    alt={titulo}
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
            </Box>
            <Typography
                variant="h5"
                component="h3"
                gutterBottom
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#402620",
                    mt: 1,
                }}
            >
                {titulo}
            </Typography>
            <Typography
                variant="body1"
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#666",
                    paddingX: "10px",
                }}
            >
                {descricao}
            </Typography>
        </Box>
    );
};

export default CardPersonalizado;
