import React from "react";
import fini from "../../assets/fini.jpg";
import { Box, Typography, Button } from "@mui/material";

const HeroSection = () => {
    return (
        <Box
            sx={{
                display: "flex",
                width: "100vw",
                minHeight: "100vh",
                overflow: "hidden",
            }}
        >
            {/* Texto à esquerda */}
            <Box
                sx={{
                    flex: 1,
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: "flex-start",
                    padding: 4,
                    backgroundColor: "#c4b3dd",
                }}
            >
                <Typography variant="h3" gutterBottom>
                    Bem-vindo à Minha Landing Page
                </Typography>
                <Typography variant="h6" gutterBottom>
                    Aqui você encontra os melhores projetos e ideias criativas.
                </Typography>
                <Button variant="contained" color="primary">
                    Saiba Mais
                </Button>
            </Box>

            {/* Imagem à direita */}
            <Box
                sx={{
                    flex: 1,
                    height: "100vh",
                    overflow: "hidden",
                }}
            >
                <img
                    src={fini}
                    alt="Fini"
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
            </Box>
        </Box>
    );
};

export default HeroSection;
