import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";

const CardPersonalizado = ({ imagem, titulo, descricao, formatoImagem }) => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md"));

    let borderRadiusStyle = {};
    // Aumenta o tamanho da imagem nos cards
    let imageContainerSize = isMobile ? "180px" : "250px"; // Tamanho maior para a imagem

    switch (formatoImagem) {
        case "quadrado":
            borderRadiusStyle = { borderRadius: "0" };
            break;
        case "portal":
            borderRadiusStyle = {
                borderTopLeftRadius: "50% 20%",
                borderTopRightRadius: "50% 20%",
                borderBottomLeftRadius: "0",
                borderBottomRightRadius: "0",
            };
            break;
        case "redondo":
            borderRadiusStyle = { borderRadius: "50%" };
            break;
        default:
            borderRadiusStyle = { borderRadius: "0" };
    }

    return (
        <Box
            sx={{
                // Largura flexível para o card:
                // Ocupa 100% em telas pequenas (xs),
                // calc(50% - gap/2) para dois cards,
                // calc(33.33% - gap*2/3) para três cards.
                // Isso permite que o gap seja aplicado no container pai.
                width: {
                    xs: "100%",
                    sm: "calc(50% - 15px)",
                    md: "calc(33.33% - 20px)",
                }, // Ajuste o 15px/20px com base no gap desejado
                maxWidth: { xs: "100%", sm: "400px" }, // Limita a largura máxima individual do card em telas sm
                padding: "20px",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                backgroundColor: "transparent",
                borderRadius: "0",
                boxShadow: "none",
                position: "relative",
            }}
        >
            <Box
                sx={{
                    width: imageContainerSize,
                    height: imageContainerSize,
                    overflow: "hidden",
                    marginBottom: "15px",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    position: "relative",
                    ...borderRadiusStyle,
                }}
            >
                <img
                    src={imagem}
                    alt={titulo}
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
                {/* Outline / Borda Suspensa */}
                <Box
                    sx={{
                        position: "absolute",
                        top: "-5px",
                        left: "-5px",
                        right: "-5px",
                        bottom: "-5px",
                        border: "2px solid #402620",
                        pointerEvents: "none",
                        zIndex: 1,
                        ...borderRadiusStyle,
                    }}
                />
            </Box>
            <Typography
                variant="h5"
                component="h3"
                gutterBottom
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#402620",
                    mt: 1,
                }}
            >
                {titulo}
            </Typography>
            <Typography
                variant="body1"
                sx={{ fontFamily: "Roboto, sans-serif", color: "#666" }}
            >
                {descricao}
            </Typography>
        </Box>
    );
};

export default CardPersonalizado;
