import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";

const CardPersonalizado = ({ imagem, titulo, descricao, formatoImagem }) => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // Reutilizando o breakpoint md

    let borderRadiusStyle = {};
    let imageContainerSize = isMobile ? "160px" : "220px"; // Tamanho maior para a imagem

    switch (formatoImagem) {
        case "quadrado":
            borderRadiusStyle = { borderRadius: "0" };
            break;
        case "portal":
            borderRadiusStyle = {
                borderTopLeftRadius: "50% 20%",
                borderTopRightRadius: "50% 20%",
                borderBottomLeftRadius: "0",
                borderBottomRightRadius: "0",
            };
            break;
        case "redondo":
            borderRadiusStyle = { borderRadius: "50%" };
            break;
        default:
            borderRadiusStyle = { borderRadius: "0" };
    }

    return (
        <Box
            sx={{
                flex: 1, // Faz com que o card ocupe o espaço disponível
                // MinWidth para 3 cards lado a lado em desktop, e 100% em mobile
                // Calc para garantir que não haja espaçamento
                minWidth: { xs: "100%", md: "calc(33.333% - 0px)" },
                // Largura máxima para evitar que fiquem muito largos em telas muito grandes
                maxWidth: { xs: "100%", md: "calc(100% / 3)" },
                padding: "20px", // Padding interno do card
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                backgroundColor: "transparent", // Remover background branco
                borderRadius: "0", // Remover borda arredondada no card em si
                boxShadow: "none", // Remover sombra do card
                // Border divisória apenas se necessário, mas para "colar", geralmente não tem no próprio card
                // As linhas divisórias podem ser adicionadas no container pai ou como pseudo-elementos.
                // Se precisar de linhas divisórias, podemos adicionar aqui:
                // borderRight: !isMobile && formatoImagem !== 'redondo' ? '1px solid rgba(0,0,0,0.1)' : 'none',
                // borderBottom: isMobile ? '1px solid rgba(0,0,0,0.1)' : 'none',
                position: "relative", // Para posicionar a borda suspensa
            }}
        >
            <Box
                sx={{
                    width: imageContainerSize,
                    height: imageContainerSize,
                    overflow: "hidden",
                    marginBottom: "15px",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    position: "relative", // Para o outline
                    ...borderRadiusStyle, // Aplica o border-radius específico do formato
                }}
            >
                <img
                    src={imagem}
                    alt={titulo}
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
                {/* Outline / Borda Suspensa */}
                <Box
                    sx={{
                        position: "absolute",
                        top: "-5px", // Ajuste para a distância do outline
                        left: "-5px", // Ajuste para a distância do outline
                        right: "-5px", // Ajuste para a distância do outline
                        bottom: "-5px", // Ajuste para a distância do outline
                        border: "2px solid #402620", // Cor e espessura da borda
                        pointerEvents: "none", // Garante que a borda não bloqueie eventos de clique na imagem
                        zIndex: 1, // Garante que fique acima da imagem
                        ...borderRadiusStyle, // Aplica o mesmo border-radius do container da imagem
                    }}
                />
            </Box>
            <Typography
                variant="h5"
                component="h3"
                gutterBottom
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#402620",
                    mt: 1,
                }}
            >
                {titulo}
            </Typography>
            <Typography
                variant="body1"
                sx={{ fontFamily: "Roboto, sans-serif", color: "#666" }}
            >
                {descricao}
            </Typography>
        </Box>
    );
};

export default CardPersonalizado;
