import React from "react";
import "@fontsource/roboto/400.css";
import fini from "../../assets/image/fini.jpg";

import {
    Box,
    Typography,
    Button,
    useTheme,
    useMediaQuery,
} from "@mui/material";

const HeroSection = () => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // md = <960px

    return (
        <Box
            sx={{
                display: "flex",
                flexDirection: isMobile ? "column" : "row", // ← muda layout
                width: "100vw",
                minHeight: "100vh",
                overflow: "hidden",
            }}
        >
            <Box
                sx={{
                    flex: 1,
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: isMobile ? "center" : "flex-start",
                    textAlign: isMobile ? "center" : "left",
                    padding: 4,
                    backgroundColor: "#c4b3dd",
                }}
            >
                <Typography
                    variant="h6"
                    gutterBottom
                    sx={{
                        fontFamily: "coco-gothic, sans-serif",
                        justifyContent: "center",
                        fontSize: isMobile ? "1rem" : "2rem",
                    }}
                >
                    BOMBONIERE MANAS
                </Typography>
                <Typography
                    variant="h3"
                    gutterBottom
                    sx={{
                        fontFamily: "tan-mon-cheri, sans-serif",
                        justifyContent: "center",
                        fontSize: isMobile ? "2.5rem" : "6rem",
                    }}
                >
                    A Maior variedade em doces da cidade
                </Typography>
                <Typography
                    variant="h6"
                    gutterBottom
                    sx={{
                        fontFamily: "coco-gothic, sans-serif", //Botar
                        justifyContent: "center",
                        fontSize: isMobile ? "1rem" : "2rem",
                    }}
                >
                    Horário de funcionamento: Seg-Sáb:
                    {/* <br /> 07:30 - 18:00 */}
                </Typography>
                <Typography
                    variant="h6"
                    gutterBottom
                    sx={{
                        justifyContent: "center",
                        fontSize: isMobile ? "1rem" : "2rem",
                    }}
                >
                    <br /> 07:30 - 18:00
                </Typography>

                <Button variant="outlined" color="#c4b3dd">
                    Faça seu pedido!
                </Button>
            </Box>

            <Box
                sx={{
                    flex: 1,
                    height: isMobile ? "300px" : "100vh", // altura menor no mobile
                    overflow: "hidden",
                }}
            >
                <img
                    src={fini}
                    alt="Fini"
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
            </Box>
        </Box>
    );
};

export default HeroSection;
