import React from "react";
import "@fontsource/roboto";
import "@fontsource/roboto/400.css";

import fini from "../../assets/image/fini.jpg";

import {
    Box,
    Typography,
    Button,
    useTheme,
    useMediaQuery,
} from "@mui/material";

const HeroSection = () => {
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md")); // md = <960px

    // --- Início das modificações para o WhatsApp ---
    const numeroWhatsApp = "5585987449279"; // **SEU NÚMERO AQUI:** código do país (55) + DDD (85 para Fortaleza) + número. Ex: 5585999998888
    const mensagemPadrao =
        "Olá, Bomboniere Manas! Gostaria de fazer um pedido.";

    // Codifica a mensagem para garantir que ela funcione corretamente na URL
    const mensagemCodificada = encodeURIComponent(mensagemPadrao);

    // Constrói o link completo do WhatsApp
    const linkWhatsApp = `https://wa.me/${numeroWhatsApp}?text=${mensagemCodificada}`;
    // --- Fim das modificações para o WhatsApp ---

    return (
        <Box
            sx={{
                display: "flex",
                flexDirection: isMobile ? "column" : "row", // ← muda layout
                width: "100vw",
                minHeight: "100vh",
                overflow: "hidden",
            }}
        >
            <Box
                sx={{
                    flex: 1,
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    alignItems: isMobile ? "center" : "flex-start",
                    textAlign: isMobile ? "center" : "left",
                    padding: 4,
                    backgroundColor: "#c4b3dd",
                }}
            >
                <Typography
                    variant="h6"
                    gutterBottom
                    sx={{
                        fontFamily: "Roboto, sans-serif ",
                        color: "#402620",
                        justifyContent: "center",
                        fontSize: isMobile ? "1rem" : "2rem",
                        alignSelf: "center",
                        textAlign: "center",
                        mt: 2,
                    }}
                >
                    BOMBONIERE MANAS
                </Typography>
                <Typography
                    variant="h3"
                    gutterBottom
                    sx={{
                        fontFamily: "tan-mon-cheri, sans-serif",
                        color: "#402620",
                        justifyContent: "center",
                        fontSize: isMobile ? "2.5rem" : "6rem",
                        mt: 12,
                        alignSelf: "center",
                        textAlign: "center",
                    }}
                >
                    A Maior variedade em doces da cidade
                </Typography>
                <Typography
                    variant="h6"
                    gutterBottom
                    sx={{
                        fontFamily: "Roboto, sans-serif ",
                        color: "#402620",
                        justifyContent: "center",
                        fontSize: isMobile ? "1rem" : "2rem",
                        alignSelf: "center",
                        textAlign: "center",
                        mt: 2,
                    }}
                >
                    Horário de funcionamento:
                    <br /> Seg - Sáb: 07:30 - 18:00
                </Typography>

                <Button
                    variant="contained" // Alterado para "contained" para dar mais destaque
                    sx={{
                        fontFamily: "Roboto, sans-serif ",
                        backgroundColor: "#402620", // Cor de fundo do botão
                        color: "#c4b3dd", // Cor do texto do botão
                        fontSize: isMobile ? "1rem" : "2rem",
                        alignSelf: "center",
                        mt: 2,
                        padding: "10px 20px", // Adiciona um pouco de padding
                        "&:hover": {
                            // Efeito de hover
                            backgroundColor: "#5a3d34",
                        },
                    }}
                    href={linkWhatsApp} // <--- AQUI o link do WhatsApp é aplicado
                    target="_blank" // <--- Abre o link em uma nova aba
                    rel="noopener noreferrer" // <--- Importante para segurança
                >
                    Faça seu pedido!
                </Button>
            </Box>

            <Box
                sx={{
                    flex: 1,
                    height: isMobile ? "300px" : "100vh", // altura menor no mobile
                    overflow: "hidden",
                }}
            >
                <img
                    src={fini}
                    alt="Fini"
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                        display: "block",
                    }}
                />
            </Box>
        </Box>
    );
};

export default HeroSection;
