import React from "react";
import { Box, Typography, useTheme, useMediaQuery } from "@mui/material";

const CardPersonalizado = ({ imagem, titulo, descricao, formatoImagem }) => {
    const theme = useTheme();
    const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm")); // Novo breakpoint para telas ainda menores

    let borderRadiusStyle = {};

    switch (formatoImagem) {
        case "quadrado":
            borderRadiusStyle = { borderRadius: "0" };
            break;
        case "portal":
            borderRadiusStyle = {
                borderTopLeftRadius: "50% 20%",
                borderTopRightRadius: "50% 20%",
                borderBottomLeftRadius: "0",
                borderBottomRightRadius: "0",
            };
            break;
        case "redondo":
            borderRadiusStyle = { borderRadius: "50%" };
            break;
        default:
            borderRadiusStyle = { borderRadius: "0" };
    }

    return (
        <Box
            sx={{
                // Largura flexível para o card:
                // Ocupa 90% em telas pequenas (xs), 45% em telas médias (sm),
                // e um tamanho fixo em telas maiores (md e acima)
                width: { xs: "90%", sm: "45%", md: "300px" },
                maxWidth: "350px", // Limita a largura máxima do card
                margin: { xs: "15px 0", md: "20px" }, // Margem vertical em mobile, todas as direções em desktop
                padding: "20px",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                textAlign: "center",
                backgroundColor: "#fff",
                borderRadius: "8px",
                boxShadow: "0px 4px 15px rgba(0, 0, 0, 0.1)",
            }}
        >
            <Box
                sx={{
                    // Tamanho do container da imagem responsivo
                    width: isSmallScreen ? "120px" : "180px",
                    height: isSmallScreen ? "120px" : "180px",
                    overflow: "hidden",
                    marginBottom: "15px",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    ...borderRadiusStyle, // Aplica o border-radius
                }}
            >
                <img
                    src={imagem}
                    alt={titulo}
                    style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                    }}
                />
            </Box>
            <Typography
                variant="h5"
                component="h3"
                gutterBottom
                sx={{
                    fontFamily: "Roboto, sans-serif",
                    color: "#402620",
                    mt: 1,
                }}
            >
                {titulo}
            </Typography>
            <Typography
                variant="body1"
                sx={{ fontFamily: "Roboto, sans-serif", color: "#666" }}
            >
                {descricao}
            </Typography>
        </Box>
    );
};

export default CardPersonalizado;
